import React, { useEffect, useState } from "react";


function SignupPage() {

    return (
        <div className="SignupPage">
            <br />
            <InputForm />
            <br />
        </div>
    );
}

function InputForm(){

    // id 
    let [ inId , setInId] = useState("input@email.com");
    const [ idLength , setIdLength ] = useState(320); // 아이디(이메일) 최대 길이

    // password
    let [ inPswd , setInPswd ] = useState("");
    const [ pswdLength , setPswdLength ] = useState(20); // 비밀번호 최대 길이

    // name
    let [ inName , setInName ] = useState("이곳에 이름을 입력해주세요.");
    const [ nameLength , setNameLength ] = useState(20); // 이름 최대 길이


    let [ sltIdResult, setSltIdResult ] = useState(0); // id 조회 결과 
    // 0 : 중복검사 미실시
    // 1 : 중복
    // 2 : 중복없음 >>> 가능

    // 아이디 검사 결과 
    let [ idChkResult, setIdChkResult] = useState("");

    // 비밀번호 검사 결과
    let [ pswdChkResult, setPswdChkResult] = useState("");

    // 이름 검사 결과 
    let [ nameChkResult, setNameChkResult] = useState("");

    let [ pswdResult, setPswdResult ] = useState(""); // 비밀번호 양식 검사 결과 


    let [ formResult, setFormResult ] = useState(0); // form 양식 검사 결과 
    // 0 : 검사 미실시
    // 1 : 검사 결과 불통과
    // 2 : 검사 결과 통과 

    // input 값 state에 즉시 반영 
    function ifChange(e){
        if (e.target.id === "inputId")
        {
            setInId(e.target.value);
        }
        else if (e.target.id === "inputPassword")
        {
            setInPswd(e.target.value);
        }
        else if (e.target.id === "inputName")
        {
            setInName(e.target.value);   
        }
        return;
    }

    // 중복검사
    /* db 에 id를 조회해 id 검색결과를 반환해 주는 함수 */
    function selectId(){
        // id 길이 검사 


        // 중복검사 


        return ;
    }

    function chkPswd(){


        return;
    }

    // db 등록 전 최종양식검사 
    function chkForm(){

        var chkResult = 0; // 검사 결과 // 초기값 0 전체 성공시 1 
        setIdChkResult("");
        pswdChkResult("");
        nameChkResult("");

        if (sltIdResult === 0) // id 중복검사 미실시 
        {
            setIdChkResult("중복검사를 실시해주세요.");
            chkResult = 1;
        }
        else if (sltIdResult === 1) // id 중복 문제 미해결  
        {
            setIdChkResult("해당 id 가 이미 존재합니다. 중복검사를 다시 실시해주세요.");
            chkResult = 1;
        }
        else if ( inPswd.length > pswdLength ) // 비밀번호 길이 초과 
        {
            pswdChkResult("비밀번호는 " + pswdLength + "자리 이하로 설정해주세요 !!!");
            chkResult = 1;
        }
        else if ( inName.length > nameLength) // 이름 길이 초과 
        {
            nameChkResult("이름은 " + nameLength + "자리 이하로 설정해주세요 !!!");
            chkResult = 1;
        }

        return ;
    }

    return(
        <div className="InputFom">
            id (email) : <input id='inputId' type='email' onChange={ ifChange }></input>&nbsp;&nbsp;
            <button type='button' onClick={ selectId() } >{ "중복확인" }</button><br />{ idChkResult }<br />
            password : <input id='inputPassword' type='text' onChange={ ifChange }></input><br />{ pswdChkResult }
            (비밀번호 제약조건)<br /> <br />
            name : <input id='inputName' type='text' onChange={ ifChange }></input><br />{ nameChkResult }<br /><br />

            <button type='button' onClick={ chkForm }>{ "Enroll" }</button>&nbsp;&nbsp;&nbsp;&nbsp;
            <button type='button' >{ "Cancle" }</button>
        </div>
    );
}

export default SignupPage;
