            name : <input id='inputName' type='text' onChange={ ifChange }></input><br /><br />

            <button type='button' >{ "Enroll" }</button>&nbsp;&nbsp;&nbsp;&nbsp;
            <button type='button' >{ "Cancle" }</button>
        </div>
    );
}



export default SignupPage;
